package User_Input;

public class Test {

	
	static int a ;
	int b ;
	

	public void show()
	{
		int c;
		c = 15;
		System.out.println(c);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		Test t = new Test();
		t.a = 5;
		t.b = 10;
		t.show();
		System.out.println("a "+a);
		System.out.println("b "+t.b);
		
		
	}

}
